import React, { useState, useRef, useEffect } from "react";

import { Container, Row, Col } from "reactstrap";
import { useParams } from "react-router-dom";
import Helmet from "../components/Helmet/Helmet";
import CommonSection from "../components/UI/CommonSection";
import "../styles/product-details.css";
import { motion } from "framer-motion";

import ProductList from "../components/UI/ProductsList";
import { useDispatch } from "react-redux";
import { cartActions } from "../redux/slices/cartSlice";

import { toast } from "react-toastify";

import axios from "axios";

const ProductDetails = () => {
  const [tab, setTab] = useState("desc");
  const { productID } = useParams();
  const url = "https://localhost:7282/api/GetProduct/" + productID;
  const [products, setProducts] = useState([]);
  const [rating, setRating] = useState(null);
  const dispatch = useDispatch();

  const reviewUser = useRef("");
  const reviewMsg = useRef("");
  const getData = () => {
    axios.get(url).then((response) => {
      setProducts(response.item);
    });
  };

  console.log(products);
  useEffect(() => {
    getData();
  }, []);

  const {
    imgURL,
    productName,
    price,
    avgRating,
    reviews,
    description,
    shortDesc,
    category,
  } = products;

  const relatedProducts = products.filter((item) => item.category === category);

  const submitHandler = (e) => {
    e.preventDefault();

    const reviewUserName = reviewUser.current.value;
    const reviewUserMsg = reviewMsg.current.value;

    console.log(reviewUserName, reviewUserMsg, rating);

    const reviewObj = {
      userName: reviewUserName,
      text: reviewUserMsg,
      rating,
    };

    console.log(reviewObj);
    toast.success("Review Submitted");
  };

  const addToCart = () => {
    dispatch(
      cartActions.addItem({
        productID,
        image: imgURL,
        productName,
        price,
      })
    );

    toast.success("Product added successfully");
  };

  useEffect(() => {
    window.scrollTo(0, 0);
  }, [products]);

  return (
    <Helmet title={productName}>
      <CommonSection title={productName} />

      <section className="pt-0">
        <Container>
          <Row>
            <Col lg="6">
              <img src={imgURL} alt="" />
            </Col>

            <Col lg="6">
              <div className="product_details">
                <h2>{productName}</h2>
                <div className="product_rating d-flex align-items-center gap-5 mb-3">
                  <div className="product_rating">
                    <span>
                      <i class="ri-star-s-fill"></i>
                    </span>
                    <span>
                      <i class="ri-star-s-fill"></i>
                    </span>
                    <span>
                      <i class="ri-star-s-fill"></i>
                    </span>
                    <span>
                      <i class="ri-star-s-fill"></i>
                    </span>
                    <span>
                      <i class="ri-star-half-s-line"></i>
                    </span>
                  </div>

                  <p>
                    (<span>{avgRating}</span> ratings)
                  </p>
                </div>

                <div className="d-flex align-items-center gap-5">
                  <span className="product_price">â‚±{price}</span>
                  <span>Category: {category.toString()}</span>
                </div>

                <p className="mt-3">{shortDesc}</p>

                <motion.button
                  whileTap={{ scale: 1.2 }}
                  className="buy_btn"
                  onClick={addToCart}
                >
                  Add To Cart
                </motion.button>
              </div>
            </Col>
          </Row>
        </Container>

        <section>
          <Container>
            <Row>
              <Col lg="12">
                <div className="tab_wrapper d-flex align-items-center gap-5">
                  <h6
                    className={`${tab === "desc" ? "active_tab" : ""}`}
                    onClick={() => setTab("desc")}
                  >
                    Description
                  </h6>
                  <h6
                    className={`${tab === "rev" ? "active_tab" : ""}`}
                    onClick={() => setTab("rev")}
                  >
                    Reviews ({reviews.length})
                  </h6>
                </div>

                {tab === "desc" ? (
                  <div className="tab_content mt-5">
                    <p>{description}</p>
                  </div>
                ) : (
                  <div className="product_review mt-5">
                    <div className="review_wrapper">
                      <ul>
                        {reviews?.map((item, index) => (
                          <li key={index} className="mb-4">
                            <h6>John Doe</h6>
                            <span>{item.rating} (rating)</span>
                            <p>{item.text}</p>
                          </li>
                        ))}
                      </ul>

                      <div className="review_form">
                        <form action="" onSubmit={submitHandler}>
                          <h4>Leave your experience</h4>
                          <div className="form_group">
                            <input
                              type="text"
                              placeholder="Enter name"
                              ref={reviewUser}
                              required
                            />
                          </div>

                          <div className="form_group d-flex align-items-center gap-3 rating_group">
                            <motion.span
                              whileTap={{ scale: 1.2 }}
                              onClick={() => setRating(1)}
                            >
                              1<i class="ri-star-s-fill"></i>
                            </motion.span>
                            <motion.span
                              whileTap={{ scale: 1.2 }}
                              onClick={() => setRating(2)}
                            >
                              2<i class="ri-star-s-fill"></i>
                            </motion.span>
                            <motion.span
                              whileTap={{ scale: 1.2 }}
                              onClick={() => setRating(3)}
                            >
                              3<i class="ri-star-s-fill"></i>
                            </motion.span>
                            <motion.span
                              whileTap={{ scale: 1.2 }}
                              onClick={() => setRating(4)}
                            >
                              4<i class="ri-star-s-fill"></i>
                            </motion.span>
                            <motion.span
                              whileTap={{ scale: 1.2 }}
                              onClick={() => setRating(5)}
                            >
                              5<i class="ri-star-s-fill"></i>
                            </motion.span>
                          </div>

                          <div className="form_group">
                            <textarea
                              ref={reviewMsg}
                              row={4}
                              type="text"
                              placeholder="Review Message..."
                              required
                            />
                          </div>

                          <div>
                            <motion.button
                              whileTap={{ scale: 1.2 }}
                              type="submit"
                              className="buy_btn"
                            >
                              Submit
                            </motion.button>
                          </div>
                        </form>
                      </div>
                    </div>
                  </div>
                )}
              </Col>

              <Col lg="12" className="mt-5">
                <h2 className="related_title">You might also like</h2>
              </Col>

              <ProductList data={relatedProducts} />
            </Row>
          </Container>
        </section>
      </section>
    </Helmet>
  );
};

export default ProductDetails;
